package basicJava;

class Fruits {
	
	protected void display() {
		System.out.println("Apple  Mango  Banana");
	}
	
}

class Encapsulation3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// TODO Auto-generated method stub
				Fruits fruit = new Fruits();
				fruit.display();

	}

}
